SELECT interface_type as yks1, hour_min as yks2, SUM( call_count ) as yks3 FROM ( SELECT t.interface_type,CONCAT(substr(t.DATE,5,4),t.HOUR,t.MINUTE) AS hour_min,t.call_count FROM psd.psds_lg_per_min t WHERE CONCAT(t.DATE,' ',t.HOUR,':',t.MINUTE,':00')>='20200917 00:00:00' AND CONCAT(t.DATE,' ',t.HOUR,':',t.MINUTE,':00')<='20200918 00:00:00' order by t.interface_type,CONCAT(substr(t.DATE,5,4),t.HOUR,t.MINUTE) ) v WHERE interface_type in ('#','EXT','PD','PRODSTATE','PRODTOC','VAS') GROUP BY yks1, yks2